import React, { useState, useEffect } from 'react'
import { Container, Row, Col, Button, Card } from 'react-bootstrap'
import ProductCard from './ProductCard'
import axios from 'axios';
import Footer from './Footer'
import Header from './Header'
import { FaArrowLeft } from 'react-icons/fa'
import { Link } from 'react-router-dom'
import ReactPaginate from 'react-paginate';
// var BASE_URL = "http://148.72.244.170:5001"
var BASE_URL = process.env.REACT_APP_BASE_URL
console.log("API_URL", BASE_URL)


function AllNFT() {
    const [NftExplore, setExplore] = useState([]);
    async function explore() {
        console.log("fdfsdfsdf", `${BASE_URL}/getAllNFT`)
        // axios.get(`${BASE_URL}/nft/getAllNFT`).then(async (data) => {
        axios.get(`${BASE_URL}/getAllNFT`).then(async (data) => {
            console.log("rahul_nft", data.data.result)
            var trending4 = []
            // data = data.data.result()
            data = data.data.result


            for (var i = 0; i < data.length; i++) {
                // console.log("rahuldfgfdnft",data[i].id)


                // console.log("url", `${BASE_URL}/metadata/${data[i].id}.json`)
                // var a = await axios.get(`${BASE_URL}/metadata/${data[i].id}.json`)
                var obj = {
                    price: data[i].price,
                    userFullName: data[i].userFullName,
                    userProfile: data[i].userProfile,
                    username: data[i].username,
                    category: data[i].category,
                    image: data[i].image,
                    nftId: data[i].id,
                    category: data[i].category,
                    name:data[i].name

                    // metadata: a.data
                }
                trending4.push(obj)
                // console.log("trending4",trending4) 
            }
            setExplore(trending4)
        })

    }

    useEffect(() => {
        explore()
    }, [])


  // =============all nft pagination===============
  const [currentItemsNFT, setCurrentItemsNFT] = useState(null);
  const [pageCountNFT, setPageCountNFT] = useState(0);

  const [itemOffsetNFT, setItemOffsetNFT] = useState(0);
  const itemsPerPageNFT = 6
  useEffect(() => {

      const endOffsetNFT = itemOffsetNFT + itemsPerPageNFT;

      setCurrentItemsNFT(NftExplore.slice(itemOffsetNFT, endOffsetNFT));
      setPageCountNFT(Math.ceil(NftExplore.length / itemsPerPageNFT));
  }, [itemOffsetNFT, itemsPerPageNFT, NftExplore]);

  const handlePageClickNFT = (event) => {
      const newOffsetNFT = (event.selected * itemsPerPageNFT) % NftExplore.length;
      setItemOffsetNFT(newOffsetNFT);
  };
    return (
        <div>
            <Header />
                <section className='explore-section all_nft' style={{ backgroundColor: "transparent" }}>
                    <Container>

                        <Row>
                            <Col lg={12}>
                                <Link to='/' className='GoBack_btn fw-bold'><FaArrowLeft className='back-icon' />  Go Back</Link>
                            </Col>
                        </Row>
                        <Row>
                            {currentItemsNFT ?
                                currentItemsNFT.map((e, index) => {
                                    console.log("eeee", e.price)

                                    return (
                                        <Col lg={3} md={6}>
                                            <ProductCard
                                                price={e.price}
                                                image={e.image}
                                                userFullName={e.userFullName}
                                                name={e.name}
                                                username={e.username}
                                                id={e.nftId}
                                                category={e.category}
                                                userProfile={e.userProfile}
                                            />
                                        </Col>
                                    )
                                })

                                :
                                // <p>Loading......</p>
                                <div></div>
                            }
                                <div className="paginate">
                                        <ReactPaginate
                                            breakLabel="..."
                                            nextLabel=" >>"
                                            onPageChange={handlePageClickNFT}
                                            pageRangeDisplayed={3}
                                            marginPagesDisplayed={2}
                                            pageCount={pageCountNFT}
                                            previousLabel="<< "
                                            containerClassName='pagination justify-content-end'
                                            pageClassName='page-item'
                                            pageLinkClassName='page-link'
                                            previousClassName='page-item'
                                            previousLinkClassName='page-link'
                                            nextClassName='page-item'
                                            nextLinkClassName='page-link'
                                            breakClassName='page-item'
                                            breakLinkClassName='page-link'
                                            activeClassName='active'

                                        />
                                    </div>
                        </Row>
                    </Container>
                </section>
            <Footer />
        </div>
    )
}

export default AllNFT
